// SPDX-License-Identifier: MIT
// SettleMint.com

pragma solidity ^0.8.0;

import "../_library/provenance/statemachine/FiniteStateMachine.sol";
import "../_library/authentication/Secured.sol";
import "../_library/utility/metadata/IpfsFieldContainer.sol";
import "../_library/utility/metadata/FileFieldContainer.sol";
import "../_library/utility/ui/UIFieldDefinitions.sol";
import "../_library/utility/conversions/Converter.sol";

/**
 * @title BillOfLading Contract
 */
contract Orders is FiniteStateMachine, IpfsFieldContainer, FileFieldContainer, UIFieldDefinitions {
  bytes32 public constant CREATE_STATEMACHINE_ROLE = "CREATE_STATEMACHINE_ROLE";

  /**
   * SupplierContract Events
   */
  event OrderCreated(uint256 fsmIndex, address owner, address registry);
  // event BillOfLadingSubmitted(address owner, address expense, uint256 amount);
  // event BillOfLadingSubmittedWithException(address owner, address expense, uint256 amount, string comment);
  // event BillOfLadingReviewed(address owner, address expense, uint256 amount);
  // event BillOfLadingNeedsUpdate(address owner, address expense, uint256 amount, string comment);
  // event BillOfLadingAccepted(address owner, address expense, uint256 amount);
  // event BillOfLadingRejected(address owner, address expense, uint256 amount, string comment);
  // event BillOfLadingForceApproved(address owner, address expense, uint256 amount, string comment);
  // event AmountChanged(address sender, uint256 fromAmount, uint256 toAmount);
  // event ProofChanged(address sender, string fromProof, string toProof);

  /**
   * @dev State Constants
   */
  bytes32 private constant STATE_ORDER_PLACED = "ORDER_PLACED";
  bytes32 private constant STATE_ORDER_ACCEPTED = "ORDER_ACCEPTED";
  bytes32 private constant STATE_ORDER_DENIED = "ORDER_DENIED";
  bytes32 private constant STATE_ORDER_SHIPPED = "ORDER_SHIPPED";
  bytes32 private constant STATE_SHIPMENT_RECEIVED = "SHIPMENT_RECEIVED";
  bytes32 private constant STATE_SHIPMENT_ACCEPTED = "SHIPMENT_ACCEPTED";
  bytes32 private constant STATE_SHIPMENT_DENIED = "SHIPMENT_DENIED";
  bytes32 private constant STATE_BILL_SENT = "BILL_SENT";
  bytes32 private constant STATE_BILL_RECEIVED = "BILL_RECEIVED";
  bytes32 private constant STATE_BILL_ACCEPTED = "BILL_ACCEPTED";
  bytes32 private constant STATE_BILL_DENIED = "BILL_DENIED";
  bytes32 private constant STATE_BILLED_TO_BUSINESS_UNIT = "BILLED_TO_BUSINESS_UNIT";
  bytes32 private constant STATE_PAYMENT_ISSUED = "PAYMENT_ISSUED";
  bytes32 private constant STATE_PAYMENT_RECEIVED = "STATE_PAYMENT_RECEIVED";

  /**
   * @dev Role Constants
   */
  bytes32 private constant ROLE_ADMIN = "ROLE_ADMIN";
  bytes32 private constant ROLE_SUPPLIER = "ROLE_SUPPLIER";
  bytes32 private constant ROLE_SSC = "ROLE_SSC";
  bytes32 private constant ROLE_BU = "ROLE_BU";

  /**
   * @dev BillOfLading states
   */
  bytes32[] public _allStates = [
    STATE_ORDER_PLACED,
    STATE_ORDER_ACCEPTED,
    STATE_ORDER_DENIED,
    STATE_ORDER_SHIPPED,
    STATE_SHIPMENT_RECEIVED,
    STATE_SHIPMENT_ACCEPTED,
    STATE_SHIPMENT_DENIED,
    STATE_BILL_SENT,
    STATE_BILL_RECEIVED,
    STATE_BILL_ACCEPTED,
    STATE_BILL_DENIED,
    STATE_BILLED_TO_BUSINESS_UNIT,
    STATE_PAYMENT_ISSUED,
    STATE_PAYMENT_RECEIVED
  ];

  /**
   * @dev BillOfLading roles
   */
  bytes32[] public _allRoles = [ROLE_ADMIN, ROLE_SUPPLIER, ROLE_SSC, ROLE_BU];

  /**
   * @dev BillOfLading helper role collection
   * specifies anyone who can edit an exponse
   */
  bytes32[] private _canEdit = [ROLE_ADMIN, ROLE_SSC];

  /**
   * The address of the upgradeable registry this contract belongs to.
   * Note that both this property together with the UPGRADEABLE_REGISTRY_TARGET const define a contract
   * that is part of an upgradeable registry and provides the link to the registry if required.
   */
  address public _upgradeableRegistry;

  /**
   * @dev StateMachineMeta contains meta data for every StateMachine - seeing as struct are not composable
   * and the base StateMachine struct is defined in the FiniteStateMachine abstract there's no other
   * way to compose structs than having two separte arrays where the indices match.
   *
   * @dev Mint is aware of the above and will zip these two into a single struct so statemachines
   * from the outside will look as a composition of both StateMachine and StateMachineMeta structs.
   */
  StateMachineMeta[] internal _meta;

  /**
   * @dev The index is a collection of DIDs for every state machine in the registry
   * Usually the getIndex call returns a list of contract addresses however as FSMs are
   * uniquely identified by their DID we return those instead
   */
  string[] internal _index;

  /**
   * @dev Struct defining an BillOfLading
   * @notice Gets decorated together with the StateMachine structs and forms the BillOfLading FSM
   */
  struct StateMachineMeta {
    string DID;
    address businessUnit;
    uint256 InBoneChickenPerKg;
    uint256 InBoneChickenTotal;
    uint256 WokForTwoPerPackage;
    uint256 WokForTwoTotal;
    uint256 FreeRangeChickenPerChicken;
    uint256 FreeRangeChickenTotal;
    uint256 PastaSaladPerPackage;
    uint256 PastaSaladTotal;
    uint256 orderTotal;
    string ipfsFieldContainerHash;
  }

  mapping(string => uint256) public prices;

  constructor(address gateKeeper, address registry) Secured(gateKeeper) {
    require(registry != address(0x0), "registry can not be zero");
    _upgradeableRegistry = registry;

    prices["InBoneChickenPerKg"] = 199;
    prices["WokForTwoPerPackage"] = 349;
    prices["FreeRangeChickenPerChicken"] = 499;
    prices["PastaSaladPerPackage"] = 299;
  }

  //////
  // FSM Definition Functions
  //////

  /**
   * @notice Returns initial state
   */
  function initialState() public pure override returns (bytes32) {
    return STATE_ORDER_PLACED;
  }

  /**
   * @notice Returns all possible states
   */
  function allStates() public view override returns (bytes32[] memory) {
    return _allStates;
  }

  /**
   * @notice Returns all possible roles
   */
  function allRoles() public view override returns (bytes32[] memory) {
    return _allRoles;
  }

  /**
   * @notice Retrieve all possible next states for a certain state
   * @param state state
   */
  function getNextStatesForState(bytes32 state) public pure override returns (bytes32[] memory test) {
    bytes32[] memory states;

    if (state == STATE_ORDER_PLACED) {
      states = new bytes32[](2);
      states[0] = STATE_ORDER_ACCEPTED;
      states[1] = STATE_ORDER_DENIED;
    }
    if (state == STATE_ORDER_ACCEPTED) {
      states = new bytes32[](1);
      states[0] = STATE_ORDER_SHIPPED;
    }
    if (state == STATE_ORDER_SHIPPED) {
      states = new bytes32[](1);
      states[0] = STATE_SHIPMENT_RECEIVED;
    }
    if (state == STATE_SHIPMENT_RECEIVED) {
      states = new bytes32[](2);
      states[0] = STATE_SHIPMENT_ACCEPTED;
      states[1] = STATE_SHIPMENT_DENIED;
    }
    if (state == STATE_SHIPMENT_ACCEPTED) {
      states = new bytes32[](1);
      states[0] = STATE_BILL_SENT;
    }
    if (state == STATE_BILL_SENT) {
      states = new bytes32[](1);
      states[0] = STATE_BILL_RECEIVED;
    }
    if (state == STATE_BILL_RECEIVED) {
      states = new bytes32[](2);
      states[0] = STATE_BILL_ACCEPTED;
      states[1] = STATE_BILL_DENIED;
    }
    if (state == STATE_BILL_ACCEPTED) {
      states = new bytes32[](1);
      states[0] = STATE_BILLED_TO_BUSINESS_UNIT;
    }
    if (state == STATE_BILLED_TO_BUSINESS_UNIT) {
      states = new bytes32[](1);
      states[0] = STATE_PAYMENT_ISSUED;
    }
    if (state == STATE_PAYMENT_ISSUED) {
      states = new bytes32[](1);
      states[0] = STATE_PAYMENT_RECEIVED;
    }

    return states;
  }

  /**
   * @notice Retrieve all roles that are allowed to move it to the given state
   * @param state state
   */
  function getAllowedRolesForState(bytes32 state) public pure override returns (bytes32[] memory) {
    bytes32[] memory roles;

    if (state == STATE_ORDER_PLACED) {
      roles = new bytes32[](2);
      roles[0] = ROLE_ADMIN;
      roles[1] = ROLE_BU;
    }
    if (state == STATE_ORDER_ACCEPTED) {
      roles = new bytes32[](2);
      roles[0] = ROLE_ADMIN;
      roles[1] = ROLE_SUPPLIER;
    }
    if (state == STATE_ORDER_DENIED) {
      roles = new bytes32[](2);
      roles[0] = ROLE_ADMIN;
      roles[1] = ROLE_SUPPLIER;
    }
    if (state == STATE_ORDER_SHIPPED) {
      roles = new bytes32[](2);
      roles[0] = ROLE_ADMIN;
      roles[1] = ROLE_SUPPLIER;
    }
    if (state == STATE_SHIPMENT_RECEIVED) {
      roles = new bytes32[](2);
      roles[0] = ROLE_ADMIN;
      roles[1] = ROLE_BU;
    }
    if (state == STATE_SHIPMENT_ACCEPTED) {
      roles = new bytes32[](2);
      roles[0] = ROLE_ADMIN;
      roles[1] = ROLE_BU;
    }
    if (state == STATE_SHIPMENT_DENIED) {
      roles = new bytes32[](2);
      roles[0] = ROLE_ADMIN;
      roles[1] = ROLE_BU;
    }
    if (state == STATE_BILL_SENT) {
      roles = new bytes32[](2);
      roles[0] = ROLE_ADMIN;
      roles[1] = ROLE_SUPPLIER;
    }
    if (state == STATE_BILL_RECEIVED) {
      roles = new bytes32[](2);
      roles[0] = ROLE_ADMIN;
      roles[1] = ROLE_SSC;
    }
    if (state == STATE_BILL_ACCEPTED) {
      roles = new bytes32[](2);
      roles[0] = ROLE_ADMIN;
      roles[1] = ROLE_SSC;
    }
    if (state == STATE_BILL_DENIED) {
      roles = new bytes32[](2);
      roles[0] = ROLE_ADMIN;
      roles[1] = ROLE_SSC;
    }
    if (state == STATE_BILLED_TO_BUSINESS_UNIT) {
      roles = new bytes32[](2);
      roles[0] = ROLE_ADMIN;
      roles[1] = ROLE_SSC;
    }
    if (state == STATE_PAYMENT_ISSUED) {
      roles = new bytes32[](2);
      roles[0] = ROLE_ADMIN;
      roles[1] = ROLE_SSC;
    }
    if (state == STATE_PAYMENT_RECEIVED) {
      roles = new bytes32[](2);
      roles[0] = ROLE_ADMIN;
      roles[1] = ROLE_SUPPLIER;
    }

    return roles;
  }

  /**
   * @notice Returns method signatures for functions which are allowed to execute in the given state
   * @param state state
   */
  function getAllowedFunctionsForState(bytes32 state) public view override returns (bytes4[] memory) {}

  /**
   * @notice Returns method signatures for functions that act as preconditions that must be met in order to transition to the given state
   * @param state state
   */
  function getPreconditionsForState(bytes32 state) public view override returns (bytes4[] memory) {}

  /**
   * @notice Returns method signatures for functions that will get called after transitioning to the given state
   * @param state state
   */
  function getCallbacksForState(bytes32 state) public view override returns (bytes4[] memory) {}

  /**
   * @notice Returns method signatures for functions that need to be called before transitioning to the given state
   * @param state state
   */
  function getPreFunctionForState(bytes32 state) public pure override returns (bytes4) {}

  //////
  // Factory Functions
  //////

  /**
   * @notice Create a new statemachine
   * @param ipfsFieldContainerHash IPFS hash of the metadata fields
   */
  function create(
    address businessUnit,
    uint256 InBoneChickenPerKg,
    uint256 WokForTwoPerPackage,
    uint256 FreeRangeChickenPerChicken,
    uint256 PastaSaladPerPackage,
    string memory ipfsFieldContainerHash
  ) public authWithCustomReason(CREATE_STATEMACHINE_ROLE, "Sender needs CREATE_STATEMACHINE_ROLE") {
    _registry.push();
    StateMachine storage sm = _registry[_registry.length - 1];
    sm.currentState = initialState();
    sm.createdAt = block.timestamp;
    sm.index = _registry.length - 1;

    _meta.push();
    StateMachineMeta storage meta = _meta[_meta.length - 1];
    meta.businessUnit = businessUnit;
    meta.ipfsFieldContainerHash = ipfsFieldContainerHash;
    meta.InBoneChickenPerKg = InBoneChickenPerKg;
    meta.InBoneChickenTotal = InBoneChickenPerKg * prices["InBoneChickenPerKg"];
    meta.WokForTwoPerPackage = WokForTwoPerPackage;
    meta.WokForTwoTotal = WokForTwoPerPackage * prices["WokForTwoPerPackage"];
    meta.FreeRangeChickenPerChicken = FreeRangeChickenPerChicken;
    meta.FreeRangeChickenTotal = FreeRangeChickenPerChicken * prices["FreeRangeChickenPerChicken"];
    meta.PastaSaladPerPackage = PastaSaladPerPackage;
    meta.PastaSaladTotal = PastaSaladPerPackage * prices["PastaSaladPerPackage"];
    meta.DID = generateDID(_meta.length - 1);
    meta.orderTotal = meta.InBoneChickenTotal + meta.WokForTwoTotal + meta.FreeRangeChickenTotal + meta.PastaSaladTotal;

    _index.push();
    _index[_index.length - 1] = meta.DID;

    emit OrderCreated(sm.index, msg.sender, _upgradeableRegistry);
  }

  /**
   * @notice Edit an existing statemachine
   * @param fsmIndex registry index of the statemachine struct
   * @param ipfsFieldContainerHash IPFS hash of the metadata fields
   */
  function edit(
    uint256 fsmIndex,
    address businessUnit,
    uint256 InBoneChickenPerKg,
    uint256 WokForTwoPerPackage,
    uint256 FreeRangeChickenPerChicken,
    uint256 PastaSaladPerPackage,
    string memory ipfsFieldContainerHash
  )
    public
    authManyWithCustomReason(_canEdit, "Edit requires one of roles: ROLE_ADMIN")
    doesStateMachineExists(fsmIndex)
    checkAllowedFunction(_registry[fsmIndex].currentState)
  {
    StateMachineMeta storage meta = _meta[fsmIndex];
    meta.businessUnit = businessUnit;
    meta.ipfsFieldContainerHash = ipfsFieldContainerHash;
    meta.businessUnit = businessUnit;
    meta.ipfsFieldContainerHash = ipfsFieldContainerHash;
    meta.InBoneChickenPerKg = InBoneChickenPerKg;
    meta.InBoneChickenTotal = InBoneChickenPerKg * prices["InBoneChickenPerKg"];
    meta.WokForTwoPerPackage = WokForTwoPerPackage;
    meta.WokForTwoTotal = WokForTwoPerPackage * prices["WokForTwoPerPackage"];
    meta.FreeRangeChickenPerChicken = FreeRangeChickenPerChicken;
    meta.FreeRangeChickenTotal = FreeRangeChickenPerChicken * prices["FreeRangeChickenPerChicken"];
    meta.PastaSaladPerPackage = PastaSaladPerPackage;
    meta.PastaSaladTotal = PastaSaladPerPackage * prices["PastaSaladPerPackage"];
    meta.orderTotal = meta.InBoneChickenTotal + meta.WokForTwoTotal + meta.FreeRangeChickenTotal + meta.PastaSaladTotal;
  }

  function canEdit() public view returns (bytes32[] memory) {
    return _canEdit;
  }

  //////
  // Syncable Functions
  //////

  /**
   * @notice Returns the structs composing a statemachine for given index
   * @param fsmIndex state machine index
   */
  function getByIndex(uint256 fsmIndex) public view returns (StateMachine memory item, StateMachineMeta memory meta) {
    item = _registry[fsmIndex];
    meta = _meta[fsmIndex];
  }

  /**
   * @notice Returns all state machines structs in a single fetch
   */
  function getContents() public view returns (StateMachine[] memory registry, StateMachineMeta[] memory meta) {
    registry = _registry;
    meta = _meta;
  }

  /**
   * @notice Returns the (DID) index
   */
  function getIndex() public view returns (string[] memory index) {
    return _index;
  }

  //////
  // BillOfLading Functions
  //////

  /**
   * @notice Returns the preconditions for given state
   * @param state state
   * @dev Internal helper function that returns actual function pointers so a method can
   * loop those and effectively call the precondition function
   */
  function getPreconditionFunctionsForState(bytes32 state)
    internal
    view
    override
    returns (function(uint256, bytes32, bytes32) view[] memory)
  {}

  /**
   * @notice Returns the callbacks for given state
   * @param state state
   * @dev Internal helper function that returns actual function pointers so a method can
   * loop those and effectively call the callback function
   */
  function getCallbackFunctionsForState(bytes32 state)
    internal
    override
    returns (function(uint256, bytes32, bytes32) internal[] memory)
  {}

  //////
  // UI Field Definition Functions
  //////

  /**
   * @notice Set the UI field definition hash
   * @param uiFieldDefinitionsHash IPFS hash containing the UI field definitions JSON
   */
  function setUIFieldDefinitionsHash(string memory uiFieldDefinitionsHash)
    public
    override
    authWithCustomReason(UPDATE_UIFIELDDEFINITIONS_ROLE, "Sender needs UPDATE_UIFIELDDEFINITIONS_ROLE")
  {
    _uiFieldDefinitionsHash = uiFieldDefinitionsHash;
  }

  /**
   * @notice Returns the UI field definition hash
   */
  function getUIFieldDefinitionsHash() public view override returns (string memory) {
    return _uiFieldDefinitionsHash;
  }

  //////
  // Helper Functions
  //////

  /**
   * @notice Generate state machine DID
   * @param fsmIndex state machine index
   */
  function generateDID(uint256 fsmIndex) internal view returns (string memory) {
    return string(abi.encodePacked("did:order:orders:0x", addressToString(address(this)), ":", uintToString(fsmIndex)));
  }
}

contracts/plot/Plot.sol.content:
// SPDX-License-Identifier: MIT
// SettleMint.com

pragma solidity ^0.8.0;

//TODO: Hoe Eigendom registreren op naam?

import "../_library/provenance/statemachine/StateMachine.sol";
import "../_library/authentication/Secured.sol";
import "../_library/utility/metadata/IpfsFieldContainer.sol";

contract Plot is StateMachine, IpfsFieldContainer {
  event PlotOwnerShipTransfered(address indexed from, address indexed to, address indexed plot, string caPaKey);
  event PlotOwnershipTransferRefused(address indexed from, address indexed to, address indexed plot, string caPaKey);
  event PlotApproved(address indexed plot, address indexed owner, string indexed caPaKey);
  event PlotDisapproved(address indexed plot, address indexed owner, string indexed caPaKey);
  event PlotSplitApproved(address indexed plot, address indexed owner, string indexed caPaKey);
  event PloSplitRefused(address indexed plot, address indexed owner, string indexed caPaKey);

  bytes32 public constant STATE_PLOT_SUBMITTED = "STATE_PLOT_SUBMITTED";
  bytes32 public constant STATE_PLOT_APPROVED = "STATE_PLOT_APPROVED";
  bytes32 public constant STATE_PLOT_NEEDS_UPDATE = "STATE_PLOT_NEEDS_UPDATE";
  bytes32 public constant STATE_PLOT_DISAPPROVED = "STATE_PLOT_DISAPPROVED";
  bytes32 public constant STATE_PLOT_REVIEWED = "STATE_PLOT_REVIEWED";
  bytes32 public constant STATE_TRANSFER_OWNER_REQUEST = "STATE_TRANSFER_OWNER_REQUEST";
  bytes32 public constant STATE_TRANSFER_OWNER_REVIEWED = "STATE_TRANSFER_OWNER_REVIEWED";
  bytes32 public constant STATE_TRANSFER_OWNER_NEEDS_UPD = "STATE_TRANSFER_OWNER_NEEDS_UPD";
  bytes32 public constant STATE_TRANSFER_OWNER_DISAPPROV = "STATE_TRANSFER_OWNER_DISAPPROV";
  bytes32 public constant STATE_SPLIT_REQUEST = "STATE_SPLIT_REQUEST";
  bytes32 public constant STATE_SPLIT_REVIEWED = "STATE_SPLIT_REVIEWED";
  bytes32 public constant STATE_SPLIT_NEEDS_UPDATE = "STATE_SPLIT_NEEDS_UPDATE";
  bytes32 public constant STATE_SPLIT_APPROVED = "STATE_SPLIT_APPROVED";
  bytes32 public constant STATE_SPLIT_DISAPPROVED = "STATE_SPLIT_DISAPPROVED";

  bytes32 public constant ROLE_LAND_REGISTRAR = "ROLE_LAND_REGISTRAR";
  bytes32 public constant ROLE_NOTARY = "ROLE_NOTARY";
  bytes32 public constant ROLE_ADMIN = "ROLE_ADMIN";

  bytes32[] public _roles = [ROLE_ADMIN, ROLE_LAND_REGISTRAR, ROLE_NOTARY];

  string public _uiFieldDefinitionsHash;
  address public _owner;
  string public _name;
  string public _caPaKey;
  string public _plotRejectionReason;
  string public _ownershipRejectionReason;
  string public _splitRejectionReason;

  constructor(
    address gateKeeper,
    string memory name,
    string memory caPaKey,
    address owner,
    string memory ipfsFieldContainerHash,
    string memory uiFieldDefinitionsHash
  ) Secured(gateKeeper) {
    _name = name;
    _caPaKey = caPaKey;
    _owner = owner;
    _ipfsFieldContainerHash = ipfsFieldContainerHash;
    _uiFieldDefinitionsHash = uiFieldDefinitionsHash;
    setupStateMachine();
  }

  function edit(
    string memory name,
    string memory caPaKey,
    address owner,
    string memory ipfsFieldContainerHash,
    string memory uiFieldDefinitionsHash
  ) public {
    _name = name;
    _caPaKey = caPaKey;
    _owner = owner;
    _ipfsFieldContainerHash = ipfsFieldContainerHash;
    _uiFieldDefinitionsHash = uiFieldDefinitionsHash;
  }

  function getRoles() public view returns (bytes32[] memory) {
    return _roles;
  }

  function setPlotNeedsUpdateRejectionReason(string memory rejectionReason) public checkAllowedFunction {
    setPlotRejectionReason(rejectionReason, STATE_PLOT_NEEDS_UPDATE);
  }

  function setPlotRejectedRejectionReason(string memory rejectionReason) public checkAllowedFunction {
    setPlotRejectionReason(rejectionReason, STATE_PLOT_DISAPPROVED);
  }

  function setTransferOwnershipRejectionReason(string memory rejectionReason) public checkAllowedFunction {
    setOwnerShipRejectionReason(rejectionReason, STATE_TRANSFER_OWNER_NEEDS_UPD);
  }

  function setSplitNeedsUpdateRejectionReason(string memory rejectionReason) public checkAllowedFunction {
    setPlotRejectionReason(rejectionReason, STATE_SPLIT_NEEDS_UPDATE);
  }

  function setSplitRejectedRejectionReason(string memory rejectionReason) public checkAllowedFunction {
    setPlotRejectionReason(rejectionReason, STATE_PLOT_APPROVED);
  }

  function DID() public view returns (string memory) {
    return string(abi.encodePacked("did:demo:plot:", _caPaKey));
  }

  function setupStateMachine() internal override {
    // Create all states
    createState(STATE_PLOT_SUBMITTED);
    createState(STATE_PLOT_APPROVED);
    createState(STATE_PLOT_NEEDS_UPDATE);
    createState(STATE_PLOT_DISAPPROVED);
    createState(STATE_PLOT_REVIEWED);
    createState(STATE_TRANSFER_OWNER_REQUEST);
    createState(STATE_TRANSFER_OWNER_REVIEWED);
    createState(STATE_TRANSFER_OWNER_NEEDS_UPD);
    createState(STATE_TRANSFER_OWNER_DISAPPROV);
    createState(STATE_SPLIT_REQUEST);
    createState(STATE_SPLIT_REVIEWED);
    createState(STATE_SPLIT_NEEDS_UPDATE);
    createState(STATE_SPLIT_APPROVED);
    createState(STATE_SPLIT_DISAPPROVED);

    // Add properties
    // STATE_PLOT_SUBMITTED
    addRoleForState(STATE_PLOT_SUBMITTED, ROLE_ADMIN);
    addRoleForState(STATE_PLOT_SUBMITTED, ROLE_NOTARY);
    addNextStateForState(STATE_PLOT_SUBMITTED, STATE_PLOT_REVIEWED);

    // STATE_PLOT_APPROVED
    addRoleForState(STATE_PLOT_APPROVED, ROLE_ADMIN);
    addRoleForState(STATE_PLOT_APPROVED, ROLE_LAND_REGISTRAR);
    addNextStateForState(STATE_PLOT_APPROVED, STATE_TRANSFER_OWNER_REQUEST);
    addNextStateForState(STATE_PLOT_APPROVED, STATE_SPLIT_REQUEST);

    // STATE_PLOT_NEEDS_UPDATE
    addRoleForState(STATE_PLOT_NEEDS_UPDATE, ROLE_ADMIN);
    addRoleForState(STATE_PLOT_NEEDS_UPDATE, ROLE_LAND_REGISTRAR);
    addNextStateForState(STATE_PLOT_NEEDS_UPDATE, STATE_PLOT_SUBMITTED);
    addPreConditionForState(STATE_PLOT_NEEDS_UPDATE, hasPlotRejectionReason);

    // STATE_PLOT_DISAPPROVED
    addRoleForState(STATE_PLOT_DISAPPROVED, ROLE_ADMIN);
    addRoleForState(STATE_PLOT_DISAPPROVED, ROLE_LAND_REGISTRAR);
    addPreConditionForState(STATE_PLOT_DISAPPROVED, hasPlotRejectionReason);

    // STATE_PLOT_REVIEWED
    addRoleForState(STATE_PLOT_REVIEWED, ROLE_ADMIN);
    addRoleForState(STATE_PLOT_REVIEWED, ROLE_LAND_REGISTRAR);
    addAllowedFunctionForState(STATE_PLOT_REVIEWED, this.setPlotNeedsUpdateRejectionReason.selector);
    addAllowedFunctionForState(STATE_PLOT_REVIEWED, this.setPlotRejectedRejectionReason.selector);
    addNextStateForState(STATE_PLOT_REVIEWED, STATE_PLOT_APPROVED);
    addNextStateForState(STATE_PLOT_REVIEWED, STATE_PLOT_NEEDS_UPDATE);
    addNextStateForState(STATE_PLOT_REVIEWED, STATE_PLOT_DISAPPROVED);

    // STATE_TRANSFER_OWNER_REQUEST
    addRoleForState(STATE_TRANSFER_OWNER_REQUEST, ROLE_ADMIN);
    addRoleForState(STATE_TRANSFER_OWNER_REQUEST, ROLE_NOTARY);
    addNextStateForState(STATE_TRANSFER_OWNER_REQUEST, STATE_TRANSFER_OWNER_REVIEWED);

    // STATE_TRANSFER_OWNER_REVIEWED
    addRoleForState(STATE_TRANSFER_OWNER_REVIEWED, ROLE_ADMIN);
    addRoleForState(STATE_TRANSFER_OWNER_REVIEWED, ROLE_LAND_REGISTRAR);
    addAllowedFunctionForState(STATE_TRANSFER_OWNER_REVIEWED, this.setTransferOwnershipRejectionReason.selector);
    addNextStateForState(STATE_TRANSFER_OWNER_REVIEWED, STATE_TRANSFER_OWNER_NEEDS_UPD);
    addNextStateForState(STATE_TRANSFER_OWNER_REVIEWED, STATE_TRANSFER_OWNER_DISAPPROV);

    // STATE_TRANSFER_OWNER_NEEDS_UPD
    addRoleForState(STATE_TRANSFER_OWNER_NEEDS_UPD, ROLE_ADMIN);
    addRoleForState(STATE_TRANSFER_OWNER_NEEDS_UPD, ROLE_LAND_REGISTRAR);
    addNextStateForState(STATE_TRANSFER_OWNER_NEEDS_UPD, STATE_TRANSFER_OWNER_REQUEST);
    addNextStateForState(STATE_TRANSFER_OWNER_NEEDS_UPD, STATE_PLOT_APPROVED);
    addPreConditionForState(STATE_TRANSFER_OWNER_NEEDS_UPD, hasOwnershipRejectionReason);

    // STATE_TRANSFER_OWNER_DISAPPROV
    addRoleForState(STATE_TRANSFER_OWNER_DISAPPROV, ROLE_ADMIN);
    addRoleForState(STATE_TRANSFER_OWNER_DISAPPROV, ROLE_LAND_REGISTRAR);
    addNextStateForState(STATE_TRANSFER_OWNER_DISAPPROV, STATE_PLOT_APPROVED);
    addPreConditionForState(STATE_TRANSFER_OWNER_DISAPPROV, hasOwnershipRejectionReason);

    // STATE_SPLIT_REQUEST
    addRoleForState(STATE_SPLIT_REQUEST, ROLE_ADMIN);
    addRoleForState(STATE_SPLIT_REQUEST, ROLE_NOTARY);
    addNextStateForState(STATE_SPLIT_REQUEST, STATE_SPLIT_REVIEWED);

    // STATE_SPLIT_REVIEWED
    addRoleForState(STATE_SPLIT_REVIEWED, ROLE_ADMIN);
    addRoleForState(STATE_SPLIT_REVIEWED, ROLE_LAND_REGISTRAR);
    addAllowedFunctionForState(STATE_SPLIT_REVIEWED, this.setPlotNeedsUpdateRejectionReason.selector);
    addAllowedFunctionForState(STATE_SPLIT_REVIEWED, this.setPlotRejectedRejectionReason.selector);
    addNextStateForState(STATE_SPLIT_REVIEWED, STATE_SPLIT_NEEDS_UPDATE);
    addNextStateForState(STATE_SPLIT_REVIEWED, STATE_SPLIT_APPROVED);
    addNextStateForState(STATE_SPLIT_REVIEWED, STATE_SPLIT_DISAPPROVED);

    // STATE_SPLIT_NEEDS_UPDATE
    addRoleForState(STATE_SPLIT_NEEDS_UPDATE, ROLE_ADMIN);
    addRoleForState(STATE_SPLIT_NEEDS_UPDATE, ROLE_LAND_REGISTRAR);
    addPreConditionForState(STATE_SPLIT_NEEDS_UPDATE, hasSplittRejectionReason);
    addNextStateForState(STATE_SPLIT_NEEDS_UPDATE, STATE_SPLIT_REQUEST);
    addNextStateForState(STATE_SPLIT_NEEDS_UPDATE, STATE_PLOT_APPROVED);

    // STATE_SPLIT_APPROVED
    // Once a plot has been split it cannot be used again
    addRoleForState(STATE_SPLIT_APPROVED, ROLE_ADMIN);
    addRoleForState(STATE_SPLIT_APPROVED, ROLE_LAND_REGISTRAR);

    // STATE_SPLIT_DISAPPROVED
    addRoleForState(STATE_SPLIT_DISAPPROVED, ROLE_ADMIN);
    addRoleForState(STATE_SPLIT_DISAPPROVED, ROLE_LAND_REGISTRAR);
    addPreConditionForState(STATE_SPLIT_DISAPPROVED, hasSplittRejectionReason);
    addNextStateForState(STATE_SPLIT_DISAPPROVED, STATE_PLOT_APPROVED);

    setInitialState(STATE_PLOT_SUBMITTED);
  }

  /**
   * Precondition functions
   */
  function hasPlotRejectionReason(
    bytes32, /* fromState */
    bytes32 /*toState*/
  ) internal view {
    bytes memory reasonForRejection = bytes(_plotRejectionReason);
    require(reasonForRejection.length > 0, "A reason for a rejection is required");
  }

  function setPlotRejectionReason(string memory rejectionReason, bytes32 nextState) private {
    bytes memory bytesRejectionReason = bytes(_plotRejectionReason);
    require(bytesRejectionReason.length > 0, "The rejection reason is required");
    _plotRejectionReason = rejectionReason;
    transitionState(nextState);
  }

  function hasOwnershipRejectionReason(
    bytes32, /* fromState */
    bytes32 /*toState*/
  ) internal view {
    bytes memory reasonForRejection = bytes(_ownershipRejectionReason);
    require(reasonForRejection.length > 0, "A reason for a rejection is required");
  }

  function setOwnerShipRejectionReason(string memory rejectionReason, bytes32 nextState) private {
    bytes memory bytesRejectionReason = bytes(_ownershipRejectionReason);
    require(bytesRejectionReason.length > 0, "The rejection reason is required");
    _ownershipRejectionReason = rejectionReason;
    transitionState(nextState);
  }

  function hasSplittRejectionReason(
    bytes32, /* fromState */
    bytes32 /*toState*/
  ) internal view {
    bytes memory reasonForRejection = bytes(_splitRejectionReason);
    require(reasonForRejection.length > 0, "A reason for a rejection is required");
  }

  function setSplitRejectionReason(string memory rejectionReason, bytes32 nextState) private {
    bytes memory bytesRejectionReason = bytes(_splitRejectionReason);
    require(bytesRejectionReason.length > 0, "The rejection reason is required");
    _splitRejectionReason = rejectionReason;
    transitionState(nextState);
  }
}

