// SPDX-License-Identifier: MIT
// SettleMint.com
pragma solidity ^0.8.0;

import "../../../provenance/statemachine/StateMachine.sol";

contract StateMachineImplHappy is StateMachine {
  bytes32 public constant STATE_CREATED = "CREATED";
  bytes32 public constant STATE_TEMPORARY = "TEMPORARY";
  bytes32 public constant STATE_MODIFIED = "MODIFIED";
  bytes32 public constant STATE_ACCEPTED = "ACCEPTED";
  bytes32 public constant STATE_REJECTED = "REJECTED";
  bytes32 public constant STATE_ENDED = "ENDED";

  bytes32 public constant ROLE_EDITOR = "EDITOR";
  bytes32 public constant ROLE_COUNTERPARTY = "COUNTERPARTY";

  bool public modified = false;
  bool public modificationTransitionHandled = false;

  address party1;
  address party2;
  mapping(address => bool) signatures;

  constructor(
    address gateKeeper,
    address partyOne,
    address partyTwo
  ) Secured(gateKeeper) {
    party1 = partyOne;
    party2 = partyTwo;
    setupStateMachine();
  }

  function modify() public checkAllowedFunction {
    modified = true;
  }

  function sign() public {
    signatures[msg.sender] = true;
  }

  function setupStateMachine() internal override {
    createState(STATE_CREATED);
    createState(STATE_MODIFIED);
    createState(STATE_ACCEPTED);
    createState(STATE_REJECTED);
    createState(STATE_TEMPORARY);
    createState(STATE_ENDED);

    // STATE_ENDED
    addRoleForState(STATE_ENDED, ROLE_EDITOR);

    // STATE_REJECTED
    addRoleForState(STATE_REJECTED, ROLE_COUNTERPARTY);

    // STATE_ACCEPTED
    addRoleForState(STATE_ACCEPTED, ROLE_COUNTERPARTY);
    addPreConditionForState(STATE_ACCEPTED, acceptPreCondition);
    addNextStateForState(STATE_ACCEPTED, STATE_ENDED);
    addNextStateForState(STATE_ACCEPTED, STATE_TEMPORARY);

    // STATE_MODIFIED
    addRoleForState(STATE_MODIFIED, ROLE_EDITOR);
    addAllowedFunctionForState(STATE_MODIFIED, this.modify.selector);
    addNextStateForState(STATE_MODIFIED, STATE_MODIFIED);
    addCallbackForState(STATE_MODIFIED, handleModificationTransition);
    addNextStateForState(STATE_MODIFIED, STATE_ACCEPTED);
    addNextStateForState(STATE_MODIFIED, STATE_REJECTED);

    // Adjust STATE_REJECTED
    addNextStateForState(STATE_REJECTED, STATE_MODIFIED);

    // STATE_CREATED
    addRoleForState(STATE_CREATED, ROLE_EDITOR);
    addAllowedFunctionForState(STATE_CREATED, this.modify.selector);
    addNextStateForState(STATE_CREATED, STATE_MODIFIED);
    addNextStateForState(STATE_CREATED, STATE_ACCEPTED);
    addNextStateForState(STATE_CREATED, STATE_REJECTED);
    addNextStateForState(STATE_TEMPORARY, STATE_ENDED);
    addCallbackForState(STATE_TEMPORARY, testInternalTransition);

    setInitialState(STATE_CREATED);
  }

  function handleModificationTransition(
    bytes32, /*fromState*/
    bytes32 /*toState*/
  ) internal {
    modificationTransitionHandled = true;
  }

  function acceptPreCondition(
    bytes32, /*fromState*/
    bytes32 /*toState*/
  ) internal view {
    require(signatures[party1], "Party 1 did not sign yet");
    require(signatures[party2], "Party 2 did not sign yet");
  }

  function testInternalTransition(
    bytes32, /*fromState*/
    bytes32 /*toState*/
  ) internal {
    transitionState(STATE_ENDED);
  }
}

contracts/omnibustoken/OmnibusToken.sol.content:
// SPDX-License-Identifier: MIT
// SettleMint.com

pragma solidity ^0.8.0;

import "../_library/authentication/Secured.sol";
import "../_library/utility/metadata/IpfsFieldContainer.sol";

contract OmnibusToken is Secured, IpfsFieldContainer {
  struct TokenAccount {
    uint256 balance;
    uint256 firstTransaction;
  }

  bytes32 internal constant MINT_ROLE = "MINT_ROLE";
  bytes32 internal constant BURN_ROLE = "BURN_ROLE";

  mapping(string => TokenAccount) public balances;
  string[] public tokenHolders;
  string internal _name;
  string internal _symbol;
  uint8 internal _decimals;
  uint256 internal _tSupply;

  constructor(
    string memory name,
    string memory symbol,
    uint8 decimals,
    address gateKeeper
  ) Secured(gateKeeper) {
    _name = name;
    _symbol = symbol;
    _decimals = decimals;
  }

  struct TransactionDetail {
    string recipient;
    string sender;
    bytes32 transactionType;
    uint256 amount;
    uint256 price;
    string comment;
  }

  TransactionDetail[] public transactionHistory;
  uint256 internal totalFunds;

  function recordTransaction(
    string memory recipient,
    string memory sender,
    uint256 amount,
    uint256 price,
    bytes32 transactionType,
    string memory comment
  ) internal {
    transactionHistory.push();
    uint256 index = transactionHistory.length;
    TransactionDetail storage t = transactionHistory[index - 1];
    t.recipient = recipient;
    t.sender = sender;
    t.transactionType = transactionType;
    t.amount = amount;
    t.price = price;
    t.comment = comment;

    totalFunds + price;
  }

  event Transfer(string sender, string recipient, uint256 amount);
  event Burn(string indexed account, uint256 amount);
  event Mint(string indexed account, uint256 amount);

  /**
   * @dev Returns the name of the token.
   */
  function name() public view returns (string memory) {
    return _name;
  }

  /**
   * @dev Returns the symbol of the token, usually a shorter version of the
   * name or an ISIN.
   */
  function symbol() public view returns (string memory) {
    return _symbol;
  }

  /**
   * @dev Returns the number of decimals used to get its user representation.
   * For example, if `decimals` equals `2`, a balance of `505` tokens should
   * be displayed to a user as `5,05` (`505 / 10 ** 2`).
   *
   * Tokens usually opt for a value of 18, imitating the relationship between
   * Ether and Wei.
   *
   * NOTE: This information is only used for _display_ purposes: it in
   * no way affects any of the arithmetic of the contract, including
   * balanceOf and transfer.
   */
  function decimals() public view returns (uint8) {
    return _decimals;
  }

  /**
   * @dev Returns the amount of tokens in existence.
   */
  function totalSupply() public view returns (uint256) {
    return _tSupply;
  }

  /**
   * @dev Returns the amount of tokens owned by `account`.
   */
  function balanceOf(string memory account) public view returns (uint256) {
    return balances[account].balance;
  }

  /**
   * @dev Moves `amount` tokens from `sender` to `recipient`.
   *
   * Returns a boolean value indicating whether the operation succeeded.
   *
   * Emits a {Transfer} event.
   *
   * Requirements:
   *
   * - `sender` and `recipient` cannot be empty.
   * - `sender` must have a balance of at least `amount`.
   */
  function transfer(
    string memory sender,
    string memory recipient,
    uint256 amount,
    uint256 price
  ) public virtual returns (bool success) {
    require(bytes(sender).length > 0, "OmnibusToken: transfer from an empty account");
    require(bytes(recipient).length > 0, "OmnibusToken: transfer to  an empty account");
    if (balances[sender].firstTransaction == 0) {
      balances[sender].firstTransaction = block.timestamp;
      tokenHolders.push(sender);
    }
    if (balances[recipient].firstTransaction == 0) {
      balances[recipient].firstTransaction = block.timestamp;
      tokenHolders.push(recipient);
    }
    TokenAccount storage senderBalance = balances[sender];
    TokenAccount storage recipientBalance = balances[recipient];
    senderBalance.balance = senderBalance.balance - amount;
    recipientBalance.balance = recipientBalance.balance + amount;
    recordTransaction(recipient, sender, amount, price, bytes32("TRANSFER"), "");
    success = true;

    emit Transfer(sender, recipient, amount);
  }

  /** @dev Creates `amount` te total supply.
   *
   * Emits a {Mint} event
   *
   * Requirements
   *
   * - minted tokens must not cause the total supply to go over the cap.
   */
  function mint(
    string memory account,
    uint256 amount,
    uint256 price
  ) public virtual authWithCustomReason(MINT_ROLE, "Caller needs MINT_ROLE") returns (bool success) {
    require(bytes(account).length > 0, "OmnibusToken: mint to an empty account");
    if (balances[account].firstTransaction == 0) {
      balances[account].firstTransaction = block.timestamp;
      tokenHolders.push(account);
    }
    _tSupply = _tSupply + amount;
    TokenAccount storage accountBalance = balances[account];
    accountBalance.balance = accountBalance.balance + amount;
    recordTransaction(account, "WEALTH MANAGEMENT", amount, price, bytes32("MINT"), "");
    success = true;
    emit Mint(account, amount);
  }

  /**
   * @dev Destroys `amount` tokens from `account`, reducing the
   * total supply.
   *
   * Emits a {Burn} event.
   *
   * Requirements
   *
   * - `account` cannot be empty.
   * - `account` must have at least `amount` tokens.
   */
  function burn(
    string memory account,
    uint256 amount,
    uint256 price
  ) public virtual authWithCustomReason(BURN_ROLE, "Caller needs BURN_ROLE") returns (bool success) {
    require(bytes(account).length > 0, "OmnibusToken: burn from an empty account");
    TokenAccount storage accountBalance = balances[account];
    accountBalance.balance = accountBalance.balance - amount;
    _tSupply = _tSupply - amount;
    recordTransaction(account, "WEALTH MANAGEMENT", amount, price, bytes32("BURN"), "");
    success = true;
    emit Burn(account, amount);
  }
}

